import React, { useState } from "react";

function App() {
  const [hoveredItem, setHoveredItem] = useState(null);

  const containerStyle = {
    display: "flex",
    height: "100vh",
    margin: 0,
  };

  const leftSectionStyle = {
    backgroundColor: "#1E1F3B",
    width: "15%",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    padding: "20px 10px",
  };

  const rightSectionStyle = {
    backgroundColor: "#2C2E5A",
    width: "85%",
    padding: "20px",
    color: "white",
  };

  const menuItemStyle = (item) => ({
    padding: hoveredItem === item ? "15px 25px" : "10px 20px", // Increase padding for hovered item
    color: hoveredItem === item ? "black" : "white",
    backgroundColor: hoveredItem === item ? "white" : "#1E1F3B",
    borderRadius: "15px",
    cursor: "pointer",
    margin: "10px 0",
    transition: "background-color 0.3s, color 0.3s, padding 0.3s", // Add padding to transition
  });


  const searchBarStyle = {
    display: "flex",
    alignItems: "center",
    backgroundColor: "white",
    borderRadius: "20px",
    padding: "5px 10px",
    width: "1300px",
    marginBottom: "20px",
  };

  const inputStyle = {
    border: "none",
    outline: "none",
    flexGrow: 1,
    padding: "5px",
  };

  const iconStyle = {
    width: "25px",
    height: "25px",
    marginRight: "10px",
  };

  const breadcrumbContainerStyle = {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    width: "100%",
    fontSize: "18px",
  };

  const breadcrumbStyle = {
    flexGrow: 1,
  };

  const langDropdownStyle = {
    cursor: "pointer",
    marginLeft: "10px",
  };

  const menuItems = ["Dashboard", "Rankings", "Pollutants", "Predictions", "Settings", "Profile"];

  return (
    <div style={containerStyle}>
      <div style={leftSectionStyle}>
        {menuItems.map((item) => (
          <div
            key={item}
            style={menuItemStyle(item)}
            onMouseEnter={() => setHoveredItem(item)}
            onMouseLeave={() => setHoveredItem(null)}
          >
            {item}
          </div>
        ))}
      </div>
      <div style={rightSectionStyle}>
        <div style={searchBarStyle}>
          <input type="text" placeholder="Search by dam, reservoir, place, city..." style={inputStyle} />
          <img src="loc_pointer.png" alt="Location Icon" style={iconStyle} />
        </div>
        <div style={breadcrumbContainerStyle}>
          <div style={breadcrumbStyle}>
            Dashboard &gt; TamilNadu &gt; Chennai
          </div>
          <div style={langDropdownStyle}>
            Lang â–¼
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
