import React, { useState } from "react";

function App() {
  const [hoveredItem, setHoveredItem] = useState(null);

  const containerStyle = {
    display: "flex",
    height: "100vh",
    margin: 0,
  };

  const leftSectionStyle = {
    backgroundColor: "#1E1F3B",
    width: "15%",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center", // Center items vertically
    alignItems: "center", // Center items horizontally
  };

  const rightSectionStyle = {
    backgroundColor: "#2C2E5A",
    width: "85%",
  };

  const menuItemStyle = (item) => ({
    padding: "10px 20px", // Add padding for better hover effect
    margin: "10px 0", // Add margin to space out items
    color: hoveredItem === item ? "black" : "white",
    backgroundColor: hoveredItem === item ? "white" : "#1E1F3B",
    borderRadius: "5px", // Add border-radius for rounded corners
    cursor: "pointer",
    transition: "background-color 0.3s, color 0.3s", // Add transition for smooth hover effect
  });

  const menuItems = ["Dashboard", "Rankings", "Pollutants", "Predictions", "Settings", "Profile"];

  return (
    <div style={containerStyle}>
      <div style={leftSectionStyle}>
        {menuItems.map((item) => (
          <div
            key={item}
            style={menuItemStyle(item)}
            onMouseEnter={() => setHoveredItem(item)}
            onMouseLeave={() => setHoveredItem(null)}
          >
            {item}
          </div>
        ))}
      </div>
      <div style={rightSectionStyle}></div>
    </div>
  );
}

export default App;
