import React, { useState } from "react";

function App() {
  const [hoveredItem, setHoveredItem] = useState(null);

  const containerStyle = {
    display: "flex",
    height: "100vh",
    margin: 0,
  };

  const leftSectionStyle = {
    backgroundColor: "#1E1F3B",
    width: "100%",
    display: "flex",
    flexDirection: "row", // Change to 'row' to align items horizontally
    alignItems: "center",
    justifyContent: "center", // Center items horizontally
    padding: "20px 10px",
  };

  const rightSectionStyle = {
    backgroundColor: "#2C2E5A",
    width: "85%",
    padding: "20px",
    color: "white",
  };

  const menuItemStyle = (item) => ({
    padding: "10px 20px",
    color: hoveredItem === item ? "black" : "white",
    backgroundColor: hoveredItem === item ? "white" : "#1E1F3B",
    borderRadius: "15px",
    cursor: "pointer",
    margin: "0 10px", // Adjust margin to space out items horizontally
    transition: "background-color 0.3s, color 0.3s",
  });

  const searchBarContainerStyle = {
    display: "flex",
    justifyContent: "center",
    width: "100%",
    marginBottom: "20px",
  };

  const searchBarStyle = {
    display: "flex",
    alignItems: "center",
    backgroundColor: "white",
    borderRadius: "20px",
    padding: "5px 10px",
    width: "500px", // Adjusted width to make the search bar longer
  };

  const inputStyle = {
    border: "none",
    outline: "none",
    flexGrow: 1,
    padding: "5px",
  };

  const iconStyle = {
    width: "20px",
    height: "20px",
    marginRight: "10px", // Move the icon slightly to the left
  };

  const breadcrumbContainerStyle = {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    width: "100%",
    fontSize: "18px",
  };

  const breadcrumbStyle = {
    flexGrow: 1,
  };

  const langDropdownStyle = {
    cursor: "pointer",
    marginLeft: "10px",
  };

  const menuItems = ["Dashboard", "Rankings", "Pollutants", "Predictions", "Settings", "Profile"];

  return (
    <div style={containerStyle}>
      <div style={leftSectionStyle}>
        {menuItems.map((item) => (
          <div
            key={item}
            style={menuItemStyle(item)}
            onMouseEnter={() => setHoveredItem(item)}
            onMouseLeave={() => setHoveredItem(null)}
          >
            {item}
          </div>
        ))}
      </div>
      <div style={rightSectionStyle}>
        <div style={searchBarContainerStyle}>
          <div style={searchBarStyle}>
            <input type="text" placeholder="Search by dam, reservoir, place, city.." style={inputStyle} />
            <img src="location-icon.png" alt="Location Icon" style={iconStyle} />
          </div>
        </div>
        <div style={breadcrumbContainerStyle}>
          <div style={breadcrumbStyle}>
            Dashboard &gt; TamilNadu &gt; Chennai
          </div>
          <div style={langDropdownStyle}>
            Lang â–¼
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
