import React, { useEffect } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css'; // Import Leaflet CSS

const RiverMap = () => {
  useEffect(() => {
    // Initialize the map
    const map = L.map('map').setView([20.5937, 78.9629], 4); // Centered on India

    // Add a tile layer to the map
    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      maxZoom: 18,
      attribution: '© OpenStreetMap contributors'
    }).addTo(map);

    // Fetch river data from an API
    fetch('https://api.example.com/indian-rivers')
      .then(response => response.json())
      .then(data => {
        data.forEach(river => {
          L.geoJson(river.geojson, {
            style: function(feature) {
              return {
                color: 'blue',
                weight: 2
              };
            },
            onEachFeature: function(feature, layer) {
              layer.bindPopup(`River: ${feature.properties.name}<br>Flow: ${feature.properties.flow}`);
            }
          }).addTo(map);
        });
      })
      .catch(error => console.error('Error fetching river data:', error));
  }, []);

  return <div id="map" style={{ height: '100vh', width: '100%' }}></div>;
};

export default RiverMap;
